    @Mock
    private ClientHttpConnector clientHttpConnector;

    @Mock
    private ClientHttpResponse clientHttpResponse;

    @BeforeEach
    public void setUp() {
        WeixinTest.customizerWeixinJavaClient(WebClient.builder().clientConnector(clientHttpConnector));
        cacheManager.getCache(CacheName.ACCESS_TOKEN).put(CacheKey.TOKEN_FMT.formatted("wxd3a0f6c8176edcab"), new Weixin.AuthenticationResponse(0, null, "66_QPewa1GdoE8rvPukCmRJPpXRxQdn85FPX8Ku9KNNHjB3JdbH8MGAe0mwWC6OnMn551MYxPVUmTKQcuCVfitPwo56rTfuDtAMwH7UvCJ6gpP8Tew7coVxq5utLUYFGCbAEAABX", 7200));
    }

    @ParameterizedTest
    @ValueSource(strings = {"-1", "0", "0123456789qazwsxedcrfvtgbyhnujmikolp0123456789abcdefghijklmnopqrstuvwxyz"})
    public void testGenerateTemporalQRCode(String scene) {
        MultiValueMap<String, String> headers = new LinkedMultiValueMap<>();
        headers.add("Content-Length", "188");
        headers.add("Content-Type", "application/json; encoding=utf-8");
        String body = "{\"ticket\":\"gQFB7zwAAAAAAAAAAS5odHRwOi8vd2VpeGluLnFxLmNvbS9xLzAyaG5kaHAyeGFjU0cxY1lRcXhBY1UAAgQA9BpkAwQ8AAAA\",\"expire_seconds\":600,\"url\":\"http:\\/\\/weixin.qq.com\\/q\\/02hndhp2xacSG1cYQqxAcU\"}";

        Mockito.when(clientHttpConnector.connect(Mockito.any(), Mockito.any(), Mockito.any()))
                .thenReturn(Mono.just(clientHttpResponse));
        Mockito.when(clientHttpResponse.getStatusCode()).thenReturn(HttpStatus.OK);
        Mockito.when(clientHttpResponse.getHeaders()).thenReturn(HttpHeaders.readOnlyHttpHeaders(headers));
        Mockito.when(clientHttpResponse.getBody()).thenReturn(Flux.just(DefaultDataBufferFactory.sharedInstance.wrap(body.getBytes(StandardCharsets.UTF_8))));

        webClient.get().uri("/mp/qr?scene={scene}&ttl=600", scene)
                .exchange()
                .expectStatus().isOk()
                .expectBody()
                .jsonPath("$.ticket").exists()
                .jsonPath("$.url").exists()
                .jsonPath("$.ttl").exists();
    }

    @ParameterizedTest
    @ValueSource(strings = {"10001"})
    public void testGeneratePermanentQRCode(String scene) {
        MultiValueMap<String, String> headers = new LinkedMultiValueMap<>();
        headers.add("Content-Length", "167");
        headers.add("Content-Type", "application/json; encoding=utf-8");
        String body = "{\"ticket\":\"gQHI8DwAAAAAAAAAAS5odHRwOi8vd2VpeGluLnFxLmNvbS9xLzAyaWVjZnBMeGFjU0cxMDAwME0wM0cAAgTb9xpkAwQAAAAA\",\"url\":\"http:\\/\\/weixin.qq.com\\/q\\/02iecfpLxacSG10000M03G\"}";

        Mockito.when(clientHttpConnector.connect(Mockito.any(), Mockito.any(), Mockito.any())).thenReturn(Mono.just(clientHttpResponse));
        Mockito.when(clientHttpResponse.getStatusCode()).thenReturn(HttpStatus.OK);
        Mockito.when(clientHttpResponse.getHeaders()).thenReturn(HttpHeaders.readOnlyHttpHeaders(headers));
        Mockito.when(clientHttpResponse.getBody()).thenReturn(Flux.just(DefaultDataBufferFactory.sharedInstance.wrap(body.getBytes(StandardCharsets.UTF_8))));

        webClient.get().uri("/mp/qr?scene={scene}", scene)
                .exchange()
                .expectStatus().isOk()
                .expectBody()
                .jsonPath("$.ticket").exists()
                .jsonPath("$.url").exists()
                .jsonPath("$.ttl").doesNotExist();
    }

    @ParameterizedTest
    @ValueSource(strings = {"gQHI8DwAAAAAAAAAAS5odHRwOi8vd2VpeGluLnFxLmNvbS9xLzAyaWVjZnBMeGFjU0cxMDAwME0wM0cAAgTb9xpkAwQAAAAA", "gQFB7zwAAAAAAAAAAS5odHRwOi8vd2VpeGluLnFxLmNvbS9xLzAyaG5kaHAyeGFjU0cxY1lRcXhBY1UAAgQA9BpkAwQ8AAAA"})
    public void testGetTicket(String ticket) {
        String base64 = "";
        MultiValueMap<String, String> headers = new LinkedMultiValueMap<>();
        headers.add("accept-ranges", "bytes");
        headers.add("Content-Type", "image/jpg");

        Mockito.when(clientHttpConnector.connect(Mockito.any(), Mockito.any(), Mockito.any())).thenReturn(Mono.just(clientHttpResponse));
        Mockito.when(clientHttpResponse.getStatusCode()).thenReturn(HttpStatus.OK);
        Mockito.when(clientHttpResponse.getHeaders()).thenReturn(HttpHeaders.readOnlyHttpHeaders(headers));
        Mockito.when(clientHttpResponse.getBody()).thenReturn(Flux.just(DefaultDataBufferFactory.sharedInstance.wrap(Base64.getDecoder().decode(base64))));

        webClient.get().uri("/mp/qr?ticket={ticket}", ticket)
                .exchange()
                .expectStatus().isOk()
                .expectHeader().contentType(MediaType.IMAGE_JPEG)
                .expectBody().consumeWith(b -> Assertions.assertEquals(FileType.JPG, InputStreamUtil.lookup(b.getResponseBody())));
    }

    @Test
    public void testGenerateShortenKey() {
        MultiValueMap<String, String> headers = new LinkedMultiValueMap<>();
        headers.add("Content-Length", "57");
        headers.add("Content-Type", "application/json; encoding=utf-8");
        String body = "{\"errcode\":0,\"errmsg\":\"ok\",\"short_key\":\"EwdrJVzUJlOTWyt\"}";

        Mockito.when(clientHttpConnector.connect(Mockito.any(), Mockito.any(), Mockito.any())).thenReturn(Mono.just(clientHttpResponse));
        Mockito.when(clientHttpResponse.getStatusCode()).thenReturn(HttpStatus.OK);
        Mockito.when(clientHttpResponse.getHeaders()).thenReturn(HttpHeaders.readOnlyHttpHeaders(headers));
        Mockito.when(clientHttpResponse.getBody()).thenReturn(Flux.just(DefaultDataBufferFactory.sharedInstance.wrap(body.getBytes(StandardCharsets.UTF_8))));

        webClient.post().uri("/mp/shorten")
                .contentType(MediaType.APPLICATION_JSON)
                .bodyValue("{\"data\":\"keyAPIGenShorten4KBkeyFetchShortenkey\",\"ttl\":600}")
                .exchange()
                .expectStatus().isOk()
                .expectBody().consumeWith(x -> Assertions.assertNotNull(x.getResponseBody()));
    }

    @Test
    public void testRestoreDataFromShortKey() {
        MultiValueMap<String, String> headers = new LinkedMultiValueMap<>();
        headers.add("Content-Length", "245");
        headers.add("Content-Type", "application/json; encoding=utf-8");
        String body = "{\"errcode\":0,\"errmsg\":\"ok\",\"long_data\":\"keyAPIGenShorten4KBkeyFetchShortenkey\",\"create_time\":1679491166,\"expire_seconds\":600}";

        Mockito.when(clientHttpConnector.connect(Mockito.any(), Mockito.any(), Mockito.any())).thenReturn(Mono.just(clientHttpResponse));
        Mockito.when(clientHttpResponse.getStatusCode()).thenReturn(HttpStatus.OK);
        Mockito.when(clientHttpResponse.getHeaders()).thenReturn(HttpHeaders.readOnlyHttpHeaders(headers));
        Mockito.when(clientHttpResponse.getBody()).thenReturn(Flux.just(DefaultDataBufferFactory.sharedInstance.wrap(body.getBytes(StandardCharsets.UTF_8))));

        webClient.get().uri("/mp/shorten?key=EwdrJVzUJlOTWyt")
                .exchange()
                .expectStatus().isOk()
                .expectBody()
                .jsonPath("$.data").exists()
                .jsonPath("$.ttl").isNumber()
                .jsonPath("$.createdAt").exists();
    }

